const buyButton=document.querySelectorAll(".buy-button"),basketPopup=document.querySelector(".modal-basket"),basketClose=basketPopup.querySelectorAll(".button-cancel");for(let e of buyButton)e.addEventListener("click",function(e){e.preventDefault(),basketPopup.classList.remove("visually-hidden")});for(let e of basketClose)e.addEventListener("click",function(e){e.preventDefault(),basketPopup.classList.add("visually-hidden")});window.addEventListener("keydown",function(e){27===e.keyCode&&(basketPopup.classList.contains("visually-hidden")||(e.preventDefault(),basketPopup.classList.add("visually-hidden")))});
