const mapLink=document.querySelector(".map-icon"),mapPopup=document.querySelector(".modal-map"),mapClose=mapPopup.querySelector(".modal-close");mapLink.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.add("modal-show"),mapPopup.classList.remove("visually-hidden")}),mapClose.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.remove("modal-show"),mapPopup.classList.add("visually-hidden")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(mapPopup.classList.contains("visually-hidden")||(e.preventDefault(),mapPopup.classList.remove("modal-show"),mapPopup.classList.add("visually-hidden")))});const buyButton=document.querySelectorAll(".buy-button"),basketPopup=document.querySelector(".modal-basket"),basketClose=basketPopup.querySelectorAll(".button-cancel");for(let e of buyButton)e.addEventListener("click",function(e){e.preventDefault(),basketPopup.classList.add("modal-show")});for(let e of basketClose)e.addEventListener("click",function(e){e.preventDefault(),basketPopup.classList.remove("modal-show")});window.addEventListener("keydown",function(e){27===e.keyCode&&(basketPopup.classList.contains("modal-show")||(e.preventDefault(),basketPopup.classList.remove("modal-show")))});const contactLink=document.querySelector(".contact-us-link"),feedbackPopup=document.querySelector(".modal"),feedbackClose=feedbackPopup.querySelector(".modal-close");contactLink.addEventListener("click",function(e){e.preventDefault(),feedbackPopup.classList.add("modal-show")}),feedbackClose.addEventListener("click",function(e){e.preventDefault(),feedbackPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(feedbackPopup.classList.contains("visually-hidden")||(e.preventDefault(),feedbackPopup.classList.remove("modal-show")))});